#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="$basedir\E:\Grey\Project\Client\video-download\node_modules\.store\dir-compare@1.8.0\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="$basedir/E:/Grey/Project/Client/video-download/node_modules/.store/dir-compare@1.8.0/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$env_node_path$pathsep$new_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../../../../dir-compare@1.8.0/node_modules/dir-compare/dircompare.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../../../../dir-compare@1.8.0/node_modules/dir-compare/dircompare.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../../../../dir-compare@1.8.0/node_modules/dir-compare/dircompare.js" $args
  } else {
    & "node$exe"  "$basedir/../../../../../dir-compare@1.8.0/node_modules/dir-compare/dircompare.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
